import * as React from 'react';
import { Plugin, Getter } from '@devexpress/dx-react-core';
import { defaultDomains } from '@devexpress/dx-chart-core';
import { BasicDataProps } from '../types';

const series = [];

export const BasicData: React.SFC<BasicDataProps> = ({ data, rotated }) => (
  <Plugin name="Basis">
    <Getter name="data" value={data} />
    <Getter name="domains" value={defaultDomains} />
    <Getter name="series" value={series} />
    <Getter name="rotated" value={rotated} />
  </Plugin>
);
import * as React from 'react';
import { Plugin, Getter, Getters } from '@devexpress/dx-react-core';
import { buildScales, scaleSeriesPoints } from '@devexpress/dx-chart-core';

const getScales = ({ domains, ranges }: Getters) => buildScales(domains, ranges);

const getSeries = ({
  series, scales, rotated,
}: Getters) => scaleSeriesPoints(series, scales, rotated);

export class ChartCore extends React.PureComponent {
  render() {
    return (
      <Plugin>
        <Getter name="scales" computed={getScales} />
        <Getter name="series" computed={getSeries} />
      </Plugin>
    );
  }
}
import * as React from 'react';
import {
  TOP, BOTTOM, LEFT, RIGHT,
} from '@devexpress/dx-chart-core';
import { Plugin, Template, TemplatePlaceholder, Getter } from '@devexpress/dx-react-core';

export class AxesLayout extends React.PureComponent {
  ref = React.createRef<HTMLDivElement>();
  render() {
    return (
      <Plugin>
        <Getter name="centerDivRef" value={this.ref} />
        <Template name="canvas">
          <div
            id="center-center"
            ref={this.ref}
            style={{ display: 'flex', flexDirection: 'column', flexGrow: 1 }}
          >
            <div id={`${TOP}-axis-container`} style={{ display: 'flex', flexDirection: 'row' }}>
              <TemplatePlaceholder name={`${TOP}-${LEFT}-axis`} />
              <TemplatePlaceholder name={`${TOP}-axis`} />
              <TemplatePlaceholder name={`${TOP}-${RIGHT}-axis`} />
            </div>
            <div
              id="center-axis-container"
              style={{ display: 'flex', flexDirection: 'row', flexGrow: 1 }}
            >
              <TemplatePlaceholder name={`${LEFT}-axis`} />
              <TemplatePlaceholder />
              <TemplatePlaceholder name={`${RIGHT}-axis`} />
            </div>
            <div id={`${BOTTOM}-axis-container`} style={{ display: 'flex', flexDirection: 'row' }}>
              <TemplatePlaceholder name={`${BOTTOM}-${LEFT}-axis`} />
              <TemplatePlaceholder name={`${BOTTOM}-axis`} />
              <TemplatePlaceholder name={`${BOTTOM}-${RIGHT}-axis`} />
            </div>
          </div>
        </Template>
      </Plugin>
    );
  }
}
import * as React from 'react';
import { Plugin, Template, TemplateConnector } from '@devexpress/dx-react-core';
import { SpaceFillingRectsProps, BBoxes } from '../types';

export class SpaceFillingRects extends React.PureComponent<SpaceFillingRectsProps> {
  render() {
    const { placeholders } = this.props;
    return (
      <Plugin name="SpaceFillingRects">
        {placeholders.map(name => (
          <Template name={name} key={name}>
            <TemplateConnector>
              {({ layouts }) => {
                const key = name.slice(name.indexOf('-') + 1);
                const width = Object.keys(layouts as BBoxes).reduce((prev, cur) => {
                  if (cur.includes(key)) {
                    return prev + layouts[cur].width;
                  }
                  return prev;
                }, 0);
                return <div id={name} style={{ width }} />;
              }}
            </TemplateConnector>
          </Template>
        ))}
      </Plugin>
    );
  }
}
import * as React from 'react';
import { Plugin, Getter, Getters } from '@devexpress/dx-react-core';
import { isReadyToRenderSeries } from '@devexpress/dx-chart-core';

export class ControllerComponent extends React.PureComponent {
  isPreviousDataEmpty: boolean = true;

  readyToRenderSeriesComputed = ({
    layouts, centerDivRef, data, axesExist,
  }: Getters) => {
    const isPreviousDataEmpty = this.isPreviousDataEmpty;
    this.isPreviousDataEmpty = !data.length;
    return isReadyToRenderSeries(layouts, centerDivRef, isPreviousDataEmpty, !!axesExist);
  }

  render () {
    return (
    <Plugin name="ControllerComponent">
      <Getter name="readyToRenderSeries" computed={this.readyToRenderSeriesComputed} />
    </Plugin>
    );
  }
}
import * as React from 'react';
import { ClipPathProps } from '../types';

const EXTRA_PIXELS = 2;

export class ClipPath extends React.PureComponent<ClipPathProps> {
  render() {
    const {
      id, width, height,
    } = this.props;
    return (
      <defs>
        <clipPath id={id}>
          <rect
            x={-EXTRA_PIXELS / 2}
            y={-EXTRA_PIXELS / 2}
            width={width + EXTRA_PIXELS}
            height={height + EXTRA_PIXELS}
          />
        </clipPath>
      </defs>
    );
  }
}
import * as React from 'react';
import { Sizer, SizerProps } from '@devexpress/dx-react-core';

// It is located in a separate file only for testing purpose -
// it should actually be placed next to PaneLayout.
export class UpdatableSizer extends React.PureComponent<SizerProps> {
  ref = React.createRef<Sizer>();

  componentDidUpdate() {
    this.props.onSizeChange(this.ref.current!.getSize());
  }

  render() {
    return <Sizer ref={this.ref} {...this.props} />;
  }
}
import * as React from 'react';
import {
  Plugin,
  Getter,
  Template,
  TemplateConnector,
  TemplatePlaceholder,
} from '@devexpress/dx-react-core';
import { ClipPath } from '../templates/clip-path';

// Original *Sizer* cannot be used because it ignores (as it should do) *forceUpdate* request.
// *UpdatableSizer* implements *componentDidUpdate* and forces internal *Sizer* size calculation.
// It allows to run chart size recalculation by calling *forceUpdate* on chart instance.
import { UpdatableSizer } from '../utils/updatable-sizer';

const DIV_STYLE: React.CSSProperties = {
  flex: 1, zIndex: 1, position: 'relative', width: '100%',
};

const SVG_STYLE: React.CSSProperties = {
  position: 'absolute', left: 0, top: 0, overflow: 'visible',
};

const SizerContainer = ({ children, forwardedRef }) => (
  <div ref={forwardedRef} style={DIV_STYLE}>{children}</div>
);

let numDefs = 0;
const getUniqueId = () => {
  numDefs += 1;
  return numDefs;
};
export class PaneLayout extends React.PureComponent {
  ref = React.createRef<SVGSVGElement>();
  clipPathId = `clip_path_${getUniqueId()}`;

  render() {
    return (
      <Plugin name="PaneLayout">
        <Getter name="rootRef" value={this.ref} />
        <Getter name="clipPathId" value={this.clipPathId} />
        <Template name="canvas">
        {params => (
          <TemplateConnector>
            {({ layouts }, { changeBBox }) => {
              const { width, height } = layouts.pane;
              return (
                <UpdatableSizer
                  containerComponent={SizerContainer}
                  onSizeChange={size => changeBBox({ placeholder: 'pane', bBox: size })}
                >
                  <svg
                    ref={this.ref}
                    {...params}
                    width={width}
                    height={height}
                    style={SVG_STYLE}
                  >
                    <ClipPath id={this.clipPathId} width={width} height={height} />
                    <TemplatePlaceholder name="series" />
                  </svg>
                </UpdatableSizer>
              );
            }}
          </TemplateConnector>
          )}
        </Template>
      </Plugin>
    );
  }
}
import * as React from 'react';
import {
  Plugin,
  Getter,
  Action,
  Template,
  TemplatePlaceholder,
  createStateHelper,
  Getters,
  ActionFn,
} from '@devexpress/dx-react-core';
import { bBoxes, getRanges } from '@devexpress/dx-chart-core';
import { LayoutManagerProps, LayoutManagerState, BBoxesChange } from '../types';

const doGetRanges = ({ layouts, rotated }: Getters) => getRanges(layouts.pane, rotated);

export class LayoutManager extends React.PureComponent<LayoutManagerProps, LayoutManagerState> {
  static defaultProps: Partial<LayoutManagerProps> = {
    width: 0,
  };
  changeBBox: ActionFn<BBoxesChange>;

  constructor(props: LayoutManagerProps) {
    super(props);

    this.state = { bBoxes: { pane: { width: 0, height: 0 } } };

    const stateHelper = createStateHelper(this);
    this.changeBBox = stateHelper.applyFieldReducer.bind(
      stateHelper,
      'bBoxes',
      bBoxes,
    );
  }

  render() {
    const {
      width, height, rootComponent: Root, ...restProps
    } = this.props;
    const { bBoxes: stateBBoxes } = this.state;

    return (
      <Plugin>
        <Getter name="layouts" value={stateBBoxes} />
        <Getter name="ranges" computed={doGetRanges} />
        <Action name="changeBBox" action={this.changeBBox} />

        <Template name="root">
          <Root
            width={width!}
            height={height}
            {...restProps}
          >
            <TemplatePlaceholder name="canvas" />
          </Root>
        </Template>
      </Plugin>
    );
  }
}
import * as React from 'react';
import {
  TOP, BOTTOM, LEFT, RIGHT,
} from '@devexpress/dx-chart-core';
import { Plugin, Template, TemplatePlaceholder } from '@devexpress/dx-react-core';

export const ComponentLayout: React.SFC = () => (
  <Plugin name="ComponentLayout">
    <Template name="canvas">
      <div id={`${TOP}-container`} style={{ display: 'flex', flexDirection: 'row' }}>
        <TemplatePlaceholder name={`${TOP}-${LEFT}`} />
        <TemplatePlaceholder name={TOP} />
        <TemplatePlaceholder name={`${TOP}-${RIGHT}`} />
      </div>
      <div id="center-container" style={{ display: 'flex', flexDirection: 'row', flexGrow: 1 }}>
        <TemplatePlaceholder name={LEFT} />
        <TemplatePlaceholder />
        <TemplatePlaceholder name={RIGHT} />
      </div>
      <div id={`${BOTTOM}-container`} style={{ display: 'flex', flexDirection: 'row' }}>
        <TemplatePlaceholder name={`${BOTTOM}-${LEFT}`} />
        <TemplatePlaceholder name={BOTTOM} />
        <TemplatePlaceholder name={`${BOTTOM}-${RIGHT}`} />
      </div>
    </Template>
  </Plugin>
);
import * as React from 'react';
import { Plugin, Getter } from '@devexpress/dx-react-core';
import { PaletteProps } from '../types';

class PaletteBase extends React.PureComponent<PaletteProps> {
  render() {
    const { scheme } = this.props;
    return (
      <Plugin name="Palette">
        <Getter name="palette" value={scheme} />
      </Plugin>
    );
  }
}

export const Palette: React.ComponentType<PaletteProps> = PaletteBase;
import * as React from 'react';
import { RootLayoutProps } from '../types';

export class Root extends React.PureComponent<RootLayoutProps> {
  render() {
    const {
      children, width, height, style, ...restProps
    } = this.props;

    return (
      <div
        style={{
          ...style,
          height: `${height}px`,
          ...width ? { width: `${width}px` } : null,
        }}
        {...restProps}
      >
        {children}
      </div>
    );
  }
}
import * as React from 'react';
import { Chart } from '../types';

export class Label extends React.PureComponent<Chart.LabelProps> {
  render() {
    return (
      <text {...this.props} />
    );
  }
}
import * as React from 'react';
import { PluginHost, withComponents, PluginComponents } from '@devexpress/dx-react-core';
import {
  TOP, BOTTOM, LEFT, RIGHT,
} from '@devexpress/dx-chart-core';
import { ChartProps } from './types';

import { BasicData } from './plugins/basic-data';
import { ChartCore } from './plugins/chart-core';
import { AxesLayout } from './plugins/axes-layout';
import { SpaceFillingRects } from './plugins/space-filling-rects';
import { ControllerComponent } from './plugins/controller-component';
import { PaneLayout } from './plugins/pane-layout';
import { LayoutManager } from './plugins/layout-manager';
import { ComponentLayout } from './plugins/component-layout';
import { Palette } from './plugins/palette';
import { Root } from './templates/layout';
import { Label } from './templates/label';

const scheme = [];
const placeholders = [
  `${TOP}-${LEFT}`,
  `${TOP}-${RIGHT}`,
  `${BOTTOM}-${LEFT}`,
  `${BOTTOM}-${RIGHT}`,
  `${TOP}-${LEFT}-axis`,
  `${TOP}-${RIGHT}-axis`,
  `${BOTTOM}-${LEFT}-axis`,
  `${BOTTOM}-${RIGHT}-axis`,
];

class RawChart extends React.PureComponent<ChartProps> {
  static defaultProps: Partial<ChartProps> = {
    height: 500,
    rotated: false,
  };
  static components: PluginComponents = {
    rootComponent: 'Root',
  };

  render() {
    const {
      data,
      width,
      height,
      children,
      rotated,
      rootComponent,
      ...restProps
    } = this.props;
    return ((
      <PluginHost>
        <BasicData data={data} rotated={rotated!} />
        <Palette scheme={scheme} />
        <LayoutManager
          width={width}
          height={height!}
          rootComponent={rootComponent}
          {...restProps}
        />
        <PaneLayout />
        <AxesLayout />
        <ComponentLayout />
        <SpaceFillingRects placeholders={placeholders} />
        {children}
        <ControllerComponent />
        <ChartCore />
      </PluginHost>
    ));
  }
}

export const Chart: React.ComponentType<ChartProps> = withComponents({ Root })(RawChart);
(Chart as any).Label = Label;
import * as React from 'react';
import { Legend } from '../../types';

export class Marker extends React.PureComponent<Legend.MarkerProps> {
  render() {
    const { color, ...restProps } = this.props;
    return (
      <svg fill={color} width="10" height="10" {...restProps}>
        <circle r={5} cx={5} cy={5} {...restProps} />
      </svg>
    );
  }
}
import * as React from 'react';
import {
  Plugin,
  TemplateConnector,
  Template,
  TemplatePlaceholder,
  withComponents,
  Getters,
  PluginComponents,
} from '@devexpress/dx-react-core';
import { getLegendItems } from '@devexpress/dx-chart-core';
import { Marker } from '../templates/legend/marker';
import { LegendProps } from '../types';

class RawLegend extends React.PureComponent<LegendProps> {
  static defaultProps: Partial<LegendProps> = {
    position: 'right',
    getItems: ({ series }: Getters) => getLegendItems(series),
  };
  static components: PluginComponents = {
    rootComponent: 'Root',
    itemComponent: 'Item',
    markerComponent: 'Marker',
    labelComponent: 'Label',
  };

  render() {
    const {
      markerComponent: MarkerComponent,
      labelComponent: Label,
      rootComponent: Root,
      itemComponent: Item,
      position,
      getItems,
    } = this.props;
    const placeholder = position!;
    return (
      <Plugin name="Legend">
        <Template name={placeholder}>
          <TemplatePlaceholder />
          <TemplateConnector>
            {getters => (
              <Root name={`legend-${placeholder}`}>
                {getItems!(getters).map(({ text, color }) => (
                  <Item key={text}>
                    <MarkerComponent name={text} color={color} />
                    <Label text={text} />
                  </Item>
                ))}
              </Root>
            )}
          </TemplateConnector>
        </Template>
      </Plugin>
    );
  }
}

export const Legend: React.ComponentType<LegendProps> = withComponents({ Marker })(RawLegend);
import * as React from 'react';

import {
  Plugin,
  Template,
  TemplatePlaceholder,
  PluginComponents,
} from '@devexpress/dx-react-core';
import { TitleProps } from '../types';

class TitleBase extends React.PureComponent<TitleProps> {
  static components: PluginComponents = {
    textComponent: 'Text',
  };
  static defaultProps: Partial<TitleProps> = {
    position: 'top',
  };

  render() {
    const {
      textComponent: Text,
      text,
      position,
    } = this.props;
    const placeholder = position!;
    return (
      <Plugin name="Title">
        <Template name={placeholder}>
          <TemplatePlaceholder />
          <Text text={text} />
        </Template>
      </Plugin>
    );
  }
}
export const Title: React.ComponentType<TitleProps> = TitleBase;
import * as React from 'react';
import {
  Template,
  Plugin,
  Getter,
  TemplatePlaceholder,
  TemplateConnector,
  withComponents,
  Getters,
  PluginComponents,
} from '@devexpress/dx-react-core';
import {
  findSeriesByName, addSeries, extendDomains, getValueDomainName, ARGUMENT_DOMAIN,
} from '@devexpress/dx-chart-core';
import {
  ExtraSeriesParameters, SeriesProps, PathComponentProps, Scales,
} from '../types';

/** @internal */
export const declareSeries = <T extends SeriesProps>(
  pluginName: string,
  { components, getPointTransformer, createHitTester }: ExtraSeriesParameters,
): React.ComponentType<T> => {
  class Component extends React.PureComponent<T> {
    static components: PluginComponents;
    static defaultProps: Partial<SeriesProps> = {
      name: 'defaultSeriesName',
    };

    render() {
      const {
        name,
        argumentField,
        valueField,
        scaleName,
        seriesComponent,
        pointComponent,
        color,
        ...restProps
      } = this.props;
      const symbolName = Symbol(name);
      const seriesItem = {
        getPointTransformer,
        createHitTester,
        ...this.props,
        symbolName,
      };
      const getSeries = ({
        series,
        data,
        palette,
      }: Getters) => addSeries(series, data, palette, seriesItem, restProps);
      const getDomains = ({
        series,
        domains,
      }: Getters) => extendDomains(domains, findSeriesByName(symbolName, series));
      return (
        <Plugin name={pluginName}>
          <Getter name="series" computed={getSeries} />
          <Getter name="domains" computed={getDomains} />
          <Template name="series">
            <TemplatePlaceholder />
            <TemplateConnector>
              {({
                series, scales, animation, rotated, layouts, clipPathId, readyToRenderSeries,
              }) => {
                const { pane } = layouts;
                const currentSeries = findSeriesByName(symbolName, series);
                const currentScales: Scales = {
                  argScale: scales[ARGUMENT_DOMAIN],
                  valScale: scales[getValueDomainName(currentSeries!.scaleName)],
                };
                const Path: React.ComponentType<PathComponentProps> =
                  currentSeries.seriesComponent as any;
                return (
                  <Path
                    index={currentSeries.index}
                    pointComponent={currentSeries.pointComponent}
                    coordinates={currentSeries.points as any}
                    rotated={rotated}
                    state={currentSeries.state}
                    color={currentSeries.color}
                    scales={currentScales}
                    pane={pane}
                    clipPathId={clipPathId}
                    animation={animation}
                    readyToRenderSeries={readyToRenderSeries}
                  />
                );
              }}
            </TemplateConnector>
          </Template>
        </Plugin>
      );
    }
  }
  Component.components = {};
  if (components.Path) {
    Component.components.seriesComponent = 'Path';
  }
  if (components.Point) {
    Component.components.pointComponent = 'Point';
  }
  return withComponents(components)(Component);
};
import * as React from 'react';
import { ITargetComponent } from '@devexpress/dx-react-core';
import { CommonComponentProps } from '@devexpress/dx-chart-core';
import { RawAxisProps, ScaleProps } from '../types';

/** @internal */
export const withPatchedProps = (
  patch: <T extends CommonComponentProps | RawAxisProps | ScaleProps>(props: T) => T,
) => <K extends CommonComponentProps | RawAxisProps | ScaleProps>(
  Target: React.ComponentType<K>,
): React.ComponentType<K> => {
  class Component extends React.PureComponent<K> {
    static components = (Target as any as ITargetComponent).components;

    render() {
      const props = patch(this.props as K);
      return <Target {...props} />;
    }
  }
  return Component;
};
import * as React from 'react';
import { Plugin, Getter, Getters } from '@devexpress/dx-react-core';
import { ARGUMENT_DOMAIN, getValueDomainName, addDomain } from '@devexpress/dx-chart-core';
import { withPatchedProps } from '../utils';
import { ScaleProps, ArgumentScaleProps, ValueScaleProps } from '../types';

/** @internal */
export class Scale extends React.PureComponent<ScaleProps> {
  render() {
    const { name, factory, modifyDomain } = this.props;
    const args = { factory, modifyDomain };
    const getDomains = ({ domains }: Getters) => addDomain(domains, name!, args);
    return (
      <Plugin name="Scale">
        <Getter name="domains" computed={getDomains} />
      </Plugin>
    );
  }
}

export const ArgumentScale: React.ComponentType<ArgumentScaleProps> = withPatchedProps(props => ({
  ...props,
  name: ARGUMENT_DOMAIN,
}))(Scale);

export const ValueScale: React.ComponentType<ValueScaleProps> = withPatchedProps(props => ({
  ...props,
  name: getValueDomainName((props as ScaleProps).name),
}))(Scale);
import * as React from 'react';
import { Plugin, Getter, Getters } from '@devexpress/dx-react-core';
import { getStackedSeries, getStackedDomains } from '@devexpress/dx-chart-core';
import { StackProps, StacksOptions, OffsetFn, OrderFn } from '../types';
import {
  stackOrderNone,
  stackOffsetDiverging,
} from 'd3-shape';

const getDomains = ({ domains, series }: Getters) => getStackedDomains(domains, series);

class StackBase extends React.PureComponent<StackProps> {
  static defaultProps: Partial<StackProps> = {
    stacks: [],
    offset: stackOffsetDiverging as OffsetFn,
    order: stackOrderNone as OrderFn,
  };

  render() {
    const { stacks, offset, order } = this.props;
    const params: StacksOptions = {
      stacks: stacks!,
      offset: offset!,
      order: order!,
    };
    const getSeries = ({ series, data }: Getters) => getStackedSeries(series, data, params);
    return (
      <Plugin name="Stack">
        <Getter name="series" computed={getSeries} />
        <Getter name="domains" computed={getDomains} />
      </Plugin>
    );
  }
}

export const Stack: React.ComponentType<StackProps> = StackBase;
import * as React from 'react';
import { Plugin, Getter } from '@devexpress/dx-react-core';
import { buildAnimation, easeOutCubic } from '@devexpress/dx-chart-core';
import { AnimationProps } from '../types';

class AnimationBase extends React.PureComponent<AnimationProps> {
  static defaultProps: Partial<AnimationProps> = {
    easing: easeOutCubic,
    duration: 1000,
  };
  render() {
    const { easing, duration } = this.props;
    const buildAnimationGetter = () => buildAnimation(easing!, duration!);
    return (
      <Plugin name="Animation">
        <Getter name="animation" computed={buildAnimationGetter} />
      </Plugin>
    );
  }
}

export const Animation: React.ComponentType<AnimationProps> = AnimationBase;
import * as React from 'react';
