import { withPatchedProps } from '@devexpress/dx-react-chart';
import {
  darken, alpha, lighten, styled,
} from '@mui/material/styles';
import classNames from 'clsx';

export const getBorderColor = theme => (
  theme.palette.mode === 'light'
    ? lighten(alpha(theme.palette.divider, 1), 0.88)
    : darken(alpha(theme.palette.divider, 1), 0.68)
);

const PREFIX = 'Target';

export const classes = {
  root: `${PREFIX}-root`,
};

export const withClassName = (...args) => {
  const setClassName = ({ className, ...restProps }) => ({
    className: classNames(classes.root, className),
    ...restProps,
  });
  // TODO: First candidate to `compose` util?
  return Target => styled(withPatchedProps(setClassName)(Target))(...args);
};
import { Chart } from '@devexpress/dx-react-chart';
import { withClassName, classes } from './utils';

const styles = ({ theme }) => {
  const { fontFamily, fontSize, fontWeightLight } = theme.typography;
  return ({
    [`&.${classes.root}`]: {
      fontFamily,
      fontSize,
      fontWeight: fontWeightLight,
      display: 'flex',
      flexDirection: 'column',
      padding: '10px',
      boxSizing: 'border-box',
    },
  });
};

export const Root = withClassName(styles)(Chart.Root);
import { Chart } from '@devexpress/dx-react-chart';
import { withClassName, classes } from './utils';

const styles = ({ theme }) => {
  const { fontFamily } = theme.typography;
  return ({
    [`&.${classes.root}`]: {
      fill: theme.palette.text.secondary,
      fontFamily,
      fontSize: 12,
      fontWeight: 400,
    },
  });
};

export const Label = withClassName(styles)(Chart.Label);
import * as React from 'react';
import PropTypes from 'prop-types';
import { withComponents } from '@devexpress/dx-react-core';
import { Chart as ChartBase, Palette } from '@devexpress/dx-react-chart';
import { Root } from './templates/layout';
import { Label } from './templates/label';

const palette = ['#42A5F5', '#FF7043', '#9CCC65', '#FFCA28', '#26A69A', '#EC407A'];

const ChartWithPalette = ({ children, ...props }) => (
  <ChartBase
    {...props}
  >
    <Palette scheme={palette} />
    {children}
  </ChartBase>
);

ChartWithPalette.components = ChartBase.components;

ChartWithPalette.propTypes = {
  children: PropTypes.node.isRequired,
};

export const Chart = withComponents({ Root })(ChartWithPalette);
Chart.Label = Label;
import List from '@mui/material/List';
import { withClassName, classes } from '../utils';

const styles = () => ({
  [`&.${classes.root}`]: {
    paddingTop: 16,
    paddingBottom: 16,
  },
});

export const Root = withClassName(styles, { name: 'LegendRoot' })(List);
import * as React from 'react';
import ListItemText from '@mui/material/ListItemText';
import { withClassName, classes } from '../utils';

const styles = () => ({
  [`&.${classes.root}`]: {
    fontSize: 14,
    paddingLeft: 8,
    paddingRight: 8,
  },
});

export const Label = withClassName(styles, { name: 'LegendLabel' })(
  ({ text, ...restProps }) => <ListItemText {...restProps}>{text}</ListItemText>,
);
import ListItem from '@mui/material/ListItem';
import { withClassName, classes } from '../utils';

const styles = ({ theme }) => ({
  [`&.${classes.root}`]: {
    alignItems: 'center',
    paddingTop: theme.spacing(0.5),
    paddingBottom: theme.spacing(0.5),
  },
});

export const Item = withClassName(styles, { name: 'LegendItem' })(ListItem);
import { withComponents } from '@devexpress/dx-react-core';
import { Legend as LegendBase } from '@devexpress/dx-react-chart';
import { Root } from '../templates/legend/root';
import { Label } from '../templates/legend/label';
import { Item } from '../templates/legend/item';

export const Legend = withComponents({
  Root, Item, Label,
})(LegendBase);
import * as React from 'react';
import Typography from '@mui/material/Typography';
import { withClassName, classes } from '../utils';

const styles = () => ({
  [`&.${classes.root}`]: {
    textAlign: 'center',
    width: '100%',
    marginBottom: '20px',
  },
});

export const Text = withClassName(styles, { name: 'Title' })(
  ({ text, ...restProps }) => <Typography component="h3" variant="h5" {...restProps}>{text}</Typography>,
);
import { withComponents } from '@devexpress/dx-react-core';
import { Title as TitleBase } from '@devexpress/dx-react-chart';
import { Text } from '../templates/title/text';

export const Title = withComponents({ Text })(TitleBase);
import { ScatterSeries, withPatchedProps } from '@devexpress/dx-react-chart';
import { styled } from '@mui/material/styles';
import classNames from 'clsx';

const PREFIX = 'Point';

const classes = {
  point: `${PREFIX}-point`,
};

const setClassName = (props) => {
  if (props.state) {
    const { className, ...rest } = props;
    return {
      className: classNames(classes.point, className),
      ...rest,
    };
  }
  return props;
};

export const Point = styled(withPatchedProps(setClassName)(
  ScatterSeries.Point,
))(({ theme }) => ({
  [`&.${classes.point}`]: {
    fill: theme.palette.background.paper,
  },
}));
import { withComponents } from '@devexpress/dx-react-core';
import { ScatterSeries as ScatterSeriesBase } from '@devexpress/dx-react-chart';
import { Point } from '../templates/series/point';

export const ScatterSeries = withComponents({ Point })(ScatterSeriesBase);
import { Axis } from '@devexpress/dx-react-chart';
import { withClassName, classes } from '../utils';

const styles = () => ({
  [`&.${classes.root}`]: {
    shapeRendering: 'crispEdges',
  },
});

export const Root = withClassName(styles)(Axis.Root);
import { Axis } from '@devexpress/dx-react-chart';
import { withClassName, getBorderColor, classes } from '../utils';

const styles = ({ theme }) => ({
  [`&.${classes.root}`]: {
    stroke: getBorderColor(theme),
    shapeRendering: 'crispEdges',
  },
});

export const Tick = withClassName(styles)(Axis.Tick);
import { Axis } from '@devexpress/dx-react-chart';
import { withClassName, classes } from '../utils';

const styles = ({ theme }) => {
